package Game;
/**
 * Class to represent upgrades in the game.
 * Each upgrade has a name, cost, and the cookies per second it generates.
 */
public class Upgrade {
    private String name;
    private int cost;
    private double cookiesPerSecond;

    /**
     * Constructor to initialize upgrade properties.
     * @param name Name of the upgrade.
     * @param cost Cost of the upgrade.
     * @param cookiesPerSecond Cookies per second generated by the upgrade.
     */
    public Upgrade(String name, int cost, double cookiesPerSecond) {
        this.name = name;
        this.cost = cost;
        this.cookiesPerSecond = cookiesPerSecond;
    }

    /**
     * Getter for the name of the upgrade.
     * @return Name of the upgrade.
     */
    public String getName() {
        return name;
    }

    /**
     * Getter for the cost of the upgrade.
     * @return Cost of the upgrade.
     */
    public int getCost() {
        return cost;
    }

    /**
     * Getter for cookies per second generated by the upgrade.
     * @return Cookies per second generated by the upgrade.
     */
    public double getCookiesPerSecond() {
        return cookiesPerSecond;
    }
}
